mapper.select:
SELECT id, roastery_id, owner_id, name, description, address, latitude, longitude, phone_number, category, thumbnail_url, open_time, close_time, average_rating, review_count, visit_count, is_closed, deleted_at, created_at, updated_at FROM stores WHERE latitude BETWEEN #{boundsVo.minLatitude} AND #{boundsVo.maxLatitude} AND longitude BETWEEN #{boundsVo.minLongitude} AND #{boundsVo.maxLongitude}
AND (
category LIKE CONCAT('%', #{category}, '%')
)
AND (name LIKE CONCAT('%', #{boundsVo.keyword}, '%') OR address LIKE CONCAT('%', #{boundsVo.keyword}, '%'))
ORDER BY created_at DESC
